# coding: utf-8
"""
This script contains utility functions for using sequences.
"""

__author__ = "Caleb"
__version__ = "0.1"
__status__ = "Prototype"

import lucene

def stream_to_sequence(stream):
	"""
	Converts a PyLucene Token Stream to a sequence of tokens.
	
	*stream* (``lucene.TokenStream``) is the token stream.
	
	Returns the sequence (``list``) of tokens (``str``).
	"""
	if not isinstance(stream, lucene.TokenStream):
		raise TypeError("stream:%r is not a ``lucene.TokenStream``." % stream)
	
	term = stream.getAttribute(lucene.TermAttribute.class_)
	seq = []
	while stream.incrementToken():
		seq.append(term.term())
	return seq
